import React, { useState } from "react";
import {
  Text,
  View,
  TouchableOpacity,
} from "react-native";
import styles from "../styles/numbersStyles";

const disableYellowBox = true;

const Number = (props) => {
const [value, setValue] = useState('?');
const [number, setNumber] = useState(props.number)


const answer = () => {
  setValue(numbers <= 10 ? 1 + numbers : numbers <= 19 ? 2 + numbers - 9 : numbers <= 29 ? 3 + numbers - 19 : numbers <= 39 ? 4 + numbers - 29 : numbers <= 49 ? 5 + numbers - 39 : numbers <= 59 ? 6 + numbers - 49 : numbers <= 69 ? 7 + numbers - 59 : numbers <= 79 ? 8 + numbers - 69 : numbers <= 89 ? 9 + numbers - 79 : numbers <= 99 ? 10 + numbers - 89 : null);
};
const answer2 = () => {
  setValue(2 + numbers)
}
let numberArray = number.number;
const numbers = numberArray

  return (
    <View style={styles.mainContainer}>
        <View style={styles.mainColorsBox}>
          <View style={styles.questionBox}>
            <Text style={styles.numbersBox}>{numbers <= 10 ? <Text>1 + {numbers}</Text> : numbers <= 19 ? <Text>2 + {numbers - 9}</Text> : numbers <= 29 ? <Text>3 + {numbers - 19}</Text> : numbers <= 39 ? <Text>4 + {numbers - 29}</Text> : numbers <= 49 ? <Text>5 + {numbers - 39}</Text> : numbers <= 59 ? <Text>6 + {numbers - 49}</Text> : numbers <= 69 ? <Text>7 + {numbers - 59}</Text> : numbers <= 79 ? <Text>8 + {numbers - 69}</Text> : numbers <= 89 ? <Text>9 + {numbers - 79}</Text> : numbers <= 99 ? <Text>10 + {numbers - 89}</Text> : <Text>Great Job</Text>}</Text>
          </View>
          <View>
            <TouchableOpacity 
              // onPress={answer}
              onPress={numbers == 100 ? alert('Next up is Subtraction') : answer}
              style={styles.button}>
                <Text style={styles.equalsText}>{numbers == 100 ? <Text style={{color: 'black'}}>!!</Text> : <Text>=</Text>}</Text>
            </TouchableOpacity>
          </View>
          <View style={styles.answerBox}>
            <Text style={styles.answer}>{value}</Text>
          </View>
        </View>
    </View>
    )
}
export default Number
