import React, { useState } from "react";
import {
  Text,
  View,
  TouchableOpacity,
} from "react-native";
import styles from "../styles/numbersStyles";

const disableYellowBox = true;



const Number = (props) => {
const [value, setValue] = useState('?');
const [number, setNumber] = useState(props.number)

const answer = () => {
  setValue(numbers <= 10 ? numbers - 1 : numbers <= 20 ? numbers - 2 - 9 : numbers <= 30 ? numbers - 3 - 20 : numbers <= 40 ? numbers - 4 - 30 : numbers <= 50 ? numbers - 5 - 40 : numbers <= 60 ? numbers - 6 - 50 : numbers <= 70 ? numbers - 7 - 60 : numbers <= 80 ? numbers - 8 - 70 : numbers <= 90 ? numbers - 9 - 80 : numbers <= 100 ? numbers - 10 - 90 : null);
};
let numberArray = number.number;
console.log(numberArray)

const numbers = numberArray

  return (
    <View style={styles.mainContainer}>
        <View style={styles.mainColorsBox}>
          <View style={styles.questionBox}>
            <Text style={styles.numbersBox}>{numbers} - 1</Text>
          </View>
          <View>
            <TouchableOpacity 
              onPress={answer}
              style={styles.button}>
                <Text style={styles.equalsText}>=</Text>
            </TouchableOpacity>
          </View>
          <View style={styles.answerBox}>
            <Text style={styles.answer}>{value}</Text>
          </View>
        </View>
    </View>
    )
}
export default Number
