import React, { useState } from "react";
import {
  Text,
  View,
  TouchableOpacity,
} from "react-native";
import styles from "../styles/numbersStyles";

const disableYellowBox = true;



const Number = (props) => {
const [value, setValue] = useState('?');
const [number, setNumber] = useState(props.number)

const answer = () => {
  setValue(
    numbers <= 10 ? numbers - 1 
    : numbers <= 20 ? numbers - 2 - 10 
    : numbers <= 29 ? numbers - 3 - 18 
    : numbers <= 39 ? numbers - 4 - 26 
    : numbers <= 49 ? numbers - 5 - 39 
    : numbers <= 59 ? numbers - 6 - 49 
    : numbers <= 69 ? numbers - 7 - 59 
    : numbers <= 79 ? numbers - 8 - 69 
    : numbers <= 89 ? numbers - 9 - 79 
    : numbers <= 99 ? numbers - 10 - 89 
    : null
    );
};
let numberArray = number.number;
console.log(numberArray)

const numbers = numberArray

  return (
    <View style={styles.mainContainer}>
        <View style={styles.mainColorsBox}>
          <View style={styles.questionBox}>
            <Text style={styles.numbersBox}>{
            numbers <= 10 ? <Text>{numbers} - 1</Text> 
            : numbers <= 19 ? <Text>{numbers - 9} - 2</Text> 
            : numbers <= 29 ? <Text>{numbers - 17} - 3</Text> 
            : numbers <= 39 ? <Text>{numbers - 26} - 4</Text> 
            : numbers <= 49 ? <Text>{numbers - 35} - 5</Text> 
            : numbers <= 59 ? <Text>{numbers - 49} - 6</Text> 
            : numbers <= 69 ? <Text>{numbers - 59} - 7</Text> 
            : numbers <= 79 ? <Text>{numbers - 69} - 8</Text> 
            : numbers <= 89 ? <Text>{numbers - 79} - 9</Text> 
            : numbers <= 99 ? <Text>{numbers - 89} - 10</Text> 
            : <Text>Great Job</Text>}</Text>
          </View>
          <View>
            <TouchableOpacity 
              onPress={answer}
              style={styles.button}>
                <Text style={styles.equalsText}>=</Text>
            </TouchableOpacity>
          </View>
          <View style={styles.answerBox}>
            <Text style={styles.answer}>{value}</Text>
          </View>
        </View>
    </View>
    )
}
export default Number
